import logo from './logo.svg';
import profile from './profile.jpg';
import './App.css';
import { useState } from 'react';

function Title(props) {
  return (
    <header>
      <h1>{props.title}</h1>
    </header>
  );
}

function AboutMe() {
  return (
    <section>
      <h1>About Me</h1>
      <img src={profile} alt='Profile' width={100} />
      <p>Placeholder text</p>
    </section>
  );
}

function Skills(props) {
  return (
    <section>
      <ul>
        {props.skills.map((skill, i) => <li key={i}>{skill}</li>)}
      </ul>
    </section>
  );
}

function SecretAccess() {
  return (
    <h3>Welcome, David.</h3>
  )
}

function Contact(props) {
  // const [name, setName] = useState('');

  function clearInput() {
    setName('');
  }

  return (
    <footer>
      <h1>Contact</h1>
      <form>
        <input id='nameInput' placeholder='Name' value={name} onChange={e => setName(e.target.value)} />
        <button onClick='clearInput'>Submit</button>
      </form>

      <p>&copy; {props.year}, David's Website</p>
    </footer>
  );
}

const skills = ['HTML', 'CSS', 'JavaScript', 'React'];

function App() {
  const [name, setName] = useState('');

  return (
    <div className='App'>
      <Title title='David Strupinski' />
      <AboutMe />
      <Skills skills={skills} />
      {name == 'David' ? <SecretAccess /> : null}
      <Contact year={new Date().getFullYear()} />
    </div>
  );
}

export default App;
